import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Navbar from '../components/Navbar.js';
import '../styles/EventDetailsPage.css';

function EventDetailsPage() {
  const { id } = useParams();
  const [event, setEvent] = useState(null);

  useEffect(() => {
    // Obtén los detalles del evento desde el backend
    fetch(`http://localhost:5432/events/getEvent/${id}`)
      .then(response => response.json())
      .then(data => setEvent(data))
      .catch(error => console.error('Error fetching event details:', error));
  }, [id]);

  if (!event) {
    return <div>Loading...</div>;
  }

  // Actualiza el contenido del HTML de la plantilla con los datos del evento
  const updateTemplate = () => {
    document.getElementById('event-title').textContent = event.title;
    document.getElementById('event-description').textContent = event.description;
    document.getElementById('event-date').textContent = new Date(event.date).toLocaleDateString();
    
    const commentsContainer = document.getElementById('comments-container');
    commentsContainer.innerHTML = ''; // Limpia los comentarios anteriores
    event.comments.forEach((comment, index) => {
      const commentElement = document.createElement('div');
      commentElement.className = 'comment';
      commentElement.innerHTML = `
        <div class="comment-user">
          <img src="../assets/user-avatar.png" alt="Usuario ${index + 1}" class="comment-avatar" />
          <p>${comment.author}</p>
        </div>
        <p>${comment.text}</p>
      `;
      commentsContainer.appendChild(commentElement);
    });
  };

  useEffect(() => {
    if (event) {
      updateTemplate();
    }
  }, [event]);

  return (
    <div className="event-details-page">
      <Navbar />
      <div className="details-container">
        <div className="event-detail-card">
          <h1 id="event-title"></h1>
          <p id="event-description"></p>
          <p id="event-date"></p>
        </div>
        <div className="comments-section">
          <h3>Comentarios</h3>
          <div id="comments-container"></div>
          <div className="comment-form">
            <div className="comment-user">
              <img src="../assets/user-avatar.png" alt="Usuario" className="comment-avatar" />
              <p>USUARIO</p>
            </div>
            <textarea placeholder="Escribe un comentario..." className="comment-input"></textarea>
            <button className="comment-button">Comentar</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default EventDetailsPage;
