import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Navbar from '../components/Navbar.js';
import EventDetailCard from '../components/EventDetailCard';
import '../styles/EventDetailsPage.css';

function EventDetailsPage() {
  const { id } = useParams();
  const [event, setEvent] = useState(null);
  const [commentText, setCommentText] = useState('');

  useEffect(() => {
    // Obtén los detalles del evento desde el backend
    fetch(`http://localhost:5432/events/getEvent/${id}`)
      .then(response => response.json())
      .then(data => setEvent(data))
      .catch(error => console.error('Error fetching event details:', error));
  }, [id]);

  const handleAddComment = () => {
    // Crea el objeto de comentario
    const comment = {
      author: 'Usuario', // Aquí debes usar el nombre de usuario real si lo tienes
      text: commentText
    };

    // Envía una solicitud POST al backend para agregar el comentario al evento
    fetch(`http://localhost:5432/comments/event/${id}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(comment)
    })
    .then(response => response.json())
    .then(data => {
      // Actualiza el estado del evento con el comentario agregado
      setEvent(data);
      // Limpia el área de texto del comentario después de agregarlo
      setCommentText('');
    })
    .catch(error => console.error('Error adding comment:', error));
  };

  if (!event) {
    return <div>Loading...</div>;
  }

  return (
    <div className="event-details-page">
      <Navbar />
      <div className="details-container">
        <EventDetailCard event={event} />
        <div className="comments-section">
          <h3>Comentarios</h3>
          {event.comments.map((comment, index) => (
            <div className="comment" key={index}>
              <div className="comment-user">
                <img src="../assets/user-avatar.png" alt={`Usuario ${index + 1}`} className="comment-avatar" />
                <p>{comment.author}</p>
              </div>
              <p>{comment.text}</p>
            </div>
          ))}
          <div className="comment-form">
            <div className="comment-user">
              <img src="../assets/user-avatar.png" alt="Usuario" className="comment-avatar" />
              <p>USUARIO</p>
            </div>
            <textarea
              placeholder="Escribe un comentario..."
              className="comment-input"
              value={commentText}
              onChange={e => setCommentText(e.target.value)}
            ></textarea>
            <button className="comment-button" onClick={handleAddComment}>Comentar</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default EventDetailsPage;
