import React, { useState , useEffect} from 'react';
import '../styles/EventForm.css';
import Navbar from "../components/Navbar.js";

function EventForm() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [categories, setCategories] = useState('');
  const [date, setDate] = useState('');
  const [location, setLocation] = useState({ city: '', address: '' });
  const [faculties, setFaculties] = useState([]);
  const [programs, setPrograms] = useState([]);
  const [cities, setCities] = useState([]);
  const [selectedFaculties, setSelectedFaculties] = useState([]);
  const [selectedPrograms, setSelectedPrograms] = useState([]);

  useEffect(() => {
    // Obtener facultades, programas y ciudades desde el backend
    fetch('http://localhost:5432/faculties/getFaculty')
      .then(response => response.json())
      .then(data => setFaculties(data))
      .catch(error => console.error('Error fetching faculties:', error));

    fetch('http://localhost:5432/programs/getProgram')
      .then(response => response.json())
      .then(data => setPrograms(data))
      .catch(error => console.error('Error fetching programs:', error));

    fetch('http://localhost:5432/cities/getCity')
      .then(response => response.json())
      .then(data => setCities(data))
      .catch(error => console.error('Error fetching cities:', error));
  }, []);
  
  console.log(cities.name)
  const handleCheckboxChange = (e, listSetter, selectedList) => {
    const { value, checked } = e.target;
    if (checked) {
      listSetter([...selectedList, value]);
    } else {
      listSetter(selectedList.filter(item => item !== value));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const newEvent = {
      title,
      description,
      categories: categories.split(',').map(category => category.trim()),
      date,
      location,
      faculties: selectedFaculties.map(facultyId => ({ id: facultyId })),
      programs: selectedPrograms.map(programId => ({ id: programId })),
      attendants: [],  // Initial empty list of attendants
      comments: []  // Initial empty list of comments
    };

    fetch('http://localhost:5432/events/create', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newEvent),
    })
      .then(response => response.json())
      .then(data => {
        if (data.success) {
          alert('Event registered successfully');
          // Clear the form
          setTitle('');
          setDescription('');
          setCategories('');
          setDate('');
          setLocation({ city: '', address: '' });
          setSelectedFaculties([]);
          setSelectedPrograms([]);
        } else {
          console.error('Error registering event:', data.message);
        }
      })
      .catch(error => console.error('Error registering event:', error));
  };

  return (
    <div className="register-event-page">
      <Navbar />
      <div className="form-container">
        <h1>Register New Event</h1>
        <form onSubmit={handleSubmit}>
          <label>
            Title:
            <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
          </label>
          <label>
            Description:
            <textarea value={description} onChange={(e) => setDescription(e.target.value)} required></textarea>
          </label>
          <label>
            Categories (comma separated):
            <input type="text" value={categories} onChange={(e) => setCategories(e.target.value)} required />
          </label>
          <label>
            Date:
            <input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
          </label>
          <label>
            Location City:
            <select value={location.city} onChange={(e) => setLocation({ ...location, city: e.target.value })} required>
              <option value="">Select a city</option>
              {cities.map(city => (
                <option key={city.code} value={city.name}>{city.name}</option>
              ))}
            </select>
          </label>
          <label>
            Location Address:
            <input type="text" value={location.address} onChange={(e) => setLocation({ ...location, address: e.target.value })} required />
          </label>
          
          <label>Faculties:</label>
          {faculties.map(faculty => (
            <div key={faculty.id}>
              <input 
                type="checkbox" 
                value={faculty.id} 
                checked={selectedFaculties.includes(faculty.id)}
                onChange={(e) => handleCheckboxChange(e, setSelectedFaculties, selectedFaculties)} 
              />
              {faculty.name}
            </div>
          ))}

          <label>Programs:</label>
          {programs.map(program => (
            <div key={program.id}>
              <input 
                type="checkbox" 
                value={program.id} 
                checked={selectedPrograms.includes(program.id)}
                onChange={(e) => handleCheckboxChange(e, setSelectedPrograms, selectedPrograms)} 
              />
              {program.name}
            </div>
          ))}

          <button type="submit">Register Event</button>
        </form>
      </div>
    </div>
  );
}

export default EventForm;
