import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Navbar from '../components/Navbar.js';
import '../styles/EventDetailsPage.css';
import Usuario from '../assets/usuario.png';
import Usuario2 from '../assets/user2.png';

function EventDetailsPage() {
  const { id } = useParams();
  const [event, setEvent] = useState(null);
  const [usuario, setUsuario] = useState('');
  const [isRegistered, setIsRegistered] = useState(false);

  useEffect(() => {
    const nombreUsuario = localStorage.getItem('cuenta');
    console.log('Nombre de usuario desde localStorage:', nombreUsuario);
    setUsuario(nombreUsuario);
  }, []);

  useEffect(() => {
    fetch(`http://localhost:5432/events/getEvent/${id}`)
      .then(response => response.json())
      .then(data => setEvent(data))
      .catch(error => console.error('Error fetching event details:', error));
  }, [id]);

  const handleRegistration = () => {
    // AquÃ­ debes hacer la llamada al backend para inscribir al usuario en el evento
    // Ejemplo de llamada al backend:
    fetch(`http://localhost:5432/events/register/${id}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ usuario }),
    })
      .then(response => response.json())
      .then(data => {
        if (data.success) {
          setIsRegistered(true);
          // Opcionalmente, puedes actualizar la lista de asistentes en el estado del evento
          setEvent(prevEvent => ({
            ...prevEvent,
            attendants: [...prevEvent.attendants, { usuario: { username: usuario }, role: 'Asistente' }],
          }));
        } else {
          console.error('Error registering for the event:', data.message);
        }
      })
      .catch(error => console.error('Error registering for the event:', error));
  };

  if (!event) {
    return <div>Loading...</div>;
  }

  return (
    <div className="event-details-page">
      <Navbar />
      <div className="details-container">
        <div className="event-detail-card">
          <h1>{event.title}</h1>
          <p><strong>Description:</strong> {event.description}</p>
          <p><strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
          <p><strong>Categories:</strong> {event.categories.join(', ')}</p>
          <p><strong>Location:</strong> {event.location ? `${event.location.city}, ${event.location.address}` : 'N/A'}</p>

          <h3>Faculties</h3>
          <ul>
            {event.faculties.map((faculty, index) => (
              <li key={index}>{faculty.name}</li>
            ))}
          </ul>

          <h3>Programs</h3>
          <ul>
            {event.programs.map((program, index) => (
              <li key={index}>{program.name}</li>
            ))}
          </ul>

          <h3>Attendants</h3>
          <ul>
            {event.attendants.map((attendant, index) => (
              <li key={index}>{attendant.usuario.username + " - " + attendant.role}</li>
            ))}
          </ul>

          {!isRegistered ? (
            <button onClick={handleRegistration} className="register-button">Inscribirse</button>
          ) : (
            <p>Ya estÃ¡s inscrito en este evento.</p>
          )}
        </div>

        <div className="comments-section">
          <h3>Comentarios</h3>
          {event.comments.map((comment, index) => (
            <div className="comment" key={index}>
              <div className="comment-user">
                <img src={Usuario2} alt={`Usuario ${index + 1}`} className="comment-avatar" />
                <p>{comment.author}</p>
              </div>
              <p>{comment.text}</p>
            </div>
          ))}

          <div className="comment-form">
            <div className="comment-user">
              <img src={Usuario} alt="Usuario" className="comment-avatar" />
              <p>{usuario ? usuario : 'Usuario'}</p>
            </div>
            <textarea placeholder="Escribe un comentario..." className="comment-input"></textarea>
            <button className="comment-button">Comentar</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default EventDetailsPage;
