import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Navbar from '../components/Navbar.js';
import '../styles/EventDetailsPage.css';

function EventDetailsPage() {
  const { id } = useParams();
  const [event, setEvent] = useState(null);
  const [usuario, setUsuario] = useState('');

  useEffect(() => {
    // Obtenemos el nombre de usuario del localStorage al cargar el componente
    const nombreUsuario = localStorage.getItem('cuenta');
    console.log('Nombre de usuario desde localStorage:', nombreUsuario); // VerificaciÃ³n
    setUsuario(nombreUsuario);
  }, []);

  useEffect(() => {
    fetch(`http://localhost:5432/events/getEvent/${id}`)
      .then(response => response.json())
      .then(data => setEvent(data))
      .catch(error => console.error('Error fetching event details:', error));
  }, [id]);

  if (!event) {
    return <div>Loading...</div>;
  }

  return (
    <div className="event-details-page">
      <Navbar />
      <div className="details-container">
        <div className="event-detail-card">
          <h1>{event.title}</h1>
          <p><strong>Description:</strong> {event.description}</p>
          <p><strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
          <p><strong>Categories:</strong> {event.categories.join(', ')}</p>
          <p><strong>Location:</strong> {event.location ? `${event.location.city}, ${event.location.address}` : 'N/A'}</p>
          
          <h3>Faculties</h3>
          <ul>
            {event.faculties.map((faculty, index) => (
              <li key={index}>{faculty.name}</li>
            ))}
          </ul>

          <h3>Programs</h3>
          <ul>
            {event.programs.map((program, index) => (
              <li key={index}>{program.name}</li>
            ))}
          </ul>

          <h3>Attendants</h3>
          <ul>
            {event.attendants.map((attendant, index) => (
              <li key={index}>{attendant.usuario.username + " - " +attendant.role}</li>
            ))} 
          </ul>
        </div>

        <div className="comments-section">
          <h3>Comentarios</h3>
          {event.comments.map((comment, index) => (
            <div className="comment" key={index}>
              <div className="comment-user">
                <img src="../assets/user-avatar.png" alt={`Usuario ${index + 1}`} className="comment-avatar" />
                <p>{comment.author}</p>
              </div>
              <p>{comment.text}</p>
            </div>
          ))}

          <div className="comment-form">
            <div className="comment-user">
              <img src="../assets/user-avatar.png" alt="Usuario" className="comment-avatar" />
              <p>{usuario ? usuario : 'Usuario'}</p>
            </div>
            <textarea placeholder="Escribe un comentario..." className="comment-input"></textarea>
            <button className="comment-button">Comentar</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default EventDetailsPage;
