import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom'; 
//import backgroundImage from '../backgroundimage.png';
import axios from 'axios';

function Login() {
  const navigate = useNavigate(); 

  const [loginData, setLoginData] = useState({
    login: '',
    password: '',
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLoginData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
    setMessage('');
  };
 
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      console.log(loginData);
      const response = await axios.post('http://localhost:5430/api/user/logear', loginData);
      const usuario = response.data;
      console.log('Usuario autenticado:', usuario);

      localStorage.setItem('token', JSON.stringify(usuario));
      console.log('token: ', localStorage.getItem('token'))
      
      /*
      1 = Admin
      2 = Viewer
      3 = Agente
      */

      if(usuario.idRol === "1"){
        console.log('validacion: ', usuario.idRol)
        navigate('/main');
      }else if(usuario.idRol === "2"){
        console.log('validacion: ', usuario.idRol)
        navigate('/mainV');
      }else{
        console.log('validacion: ', usuario.idRol)
        navigate('/mainA');
      }
      
    } catch (error) {
      console.error('Error de inicio de sesión:', error);
      if (error.response && error.response.status === 401) {
        setMessage('Credenciales inválidas');
      } else {
        setMessage('Error de inicio de sesión');
      }
    }
  };

  function KeyIcon(props) {
    return (
      <svg
        {...props}
        xmlns="http://www.w3.org/2000/svg"
        width="24"
        height="24"
        viewBox="0 0 24 24"
        fill="none"
        stroke="currentColor"
        strokeWidth="2"
        strokeLinecap="round"
        strokeLinejoin="round"
      >
        <circle cx="7.5" cy="15.5" r="5.5" />
        <path d="m21 2-9.6 9.6" />
        <path d="m15.5 7.5 3 3L22 7l-3-3" />
      </svg>
    )
  }
  
  
  function UserIcon(props) {
    return (
      <svg
        {...props}
        xmlns="http://www.w3.org/2000/svg"
        width="24"
        height="24"
        viewBox="0 0 24 24"
        fill="none"
        stroke="currentColor"
        strokeWidth="2"
        strokeLinecap="round"
        strokeLinejoin="round"
      >
        <path d="M19 21v-2a4 4 0 0 0-4-4H9a4 4 0 0 0-4 4v2" />
        <circle cx="12" cy="7" r="4" />
      </svg>
    )
  }

  function Input({ type, placeholder, name, value, onChange }) {
    return (
      <input
        type={type}
        placeholder={placeholder}
        name={name}
        value={value}
        onChange={onChange}
        className="w-full px-4 py-2 mt-2 border rounded-md focus:outline-none focus:ring-1 focus:ring-blue-600"
      />
    );
  }

  function Button({ children, className, onClick }) {
    return (
      <button
        className={`${className} px-4 py-2 mt-4 rounded-md focus:outline-none`}
        onClick={onClick}
      >
        {children}
      </button>
    );
  }

  return (
    <div className="flex h-screen bg-[#f0f2f5] justify-center items-center">
      <div className="bg-white rounded-lg shadow-lg w-[400px]">
        <div className="flex flex-col h-full">
          <div
            className="h-64 bg-cover bg-center rounded-t-lg"
            style={{ backgroundImage: "url('/placeholder.svg?height=256&width=400')" }}
          />
          <div className="flex flex-col flex-grow p-8">
            <h2 className="text-2xl font-semibold">Iniciar Sesión</h2>
            <p className="mt-2 text-sm text-gray-600">
              Si no tienes una cuenta registrada
              <a href="#" className="text-blue-600 hover:underline">
                {" "}
                Regístrate Aquí!
              </a>
            </p>
            <div className="mt-8">
              <div className="flex items-center space-x-2">
                <KeyIcon className="text-gray-400" />
                <Input type="text" placeholder="Ingresa tu código" name="codigo" value={loginData.codigo} onChange={handleChange} />
              </div>
              <div className="mt-4 flex items-center space-x-2">
                <UserIcon className="text-gray-400" />
                <Input type="text" placeholder="Ingresa tu usuario" name="usuario" value={loginData.usuario} onChange={handleChange} />
              </div>
            </div>
            <div className="mt-8">
              <Button className="w-full bg-blue-500 hover:bg-blue-700 text-white" onClick={handleLogin}>Iniciar Sesión</Button>
            </div>
            {message && <p className="mt-4 text-red-500">{message}</p>}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
