import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom'; 
//import backgroundImage from '../backgroundimage.png';
import axios from 'axios';

function Login() {
  const navigate = useNavigate(); 

  const [loginData, setLoginData] = useState({
    login: '',
    password: '',
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLoginData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
    setMessage('');
  };
 
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      console.log(loginData);
      const response = await axios.post('http://localhost:5430/api/user/logear', loginData);
      const usuario = response.data;
      console.log('Usuario autenticado:', usuario);

      localStorage.setItem('token', JSON.stringify(usuario));
      console.log('token: ', localStorage.getItem('token'))
      
      /*
      1 = Admin
      2 = Viewer
      3 = Agente
      */

      if(usuario.idRol === "1"){
        console.log('validacion: ', usuario.idRol)
        navigate('/main');
      }else if(usuario.idRol === "2"){
        console.log('validacion: ', usuario.idRol)
        navigate('/mainV');
      }else{
        console.log('validacion: ', usuario.idRol)
        navigate('/mainA');
      }
      
    } catch (error) {
      console.error('Error de inicio de sesión:', error);
      if (error.response && error.response.status === 401) {
        setMessage('Credenciales inválidas');
      } else {
        setMessage('Error de inicio de sesión');
      }
    }
  };

  function KeyIcon(props) {
    return (
      <svg
        {...props}
        xmlns="http://www.w3.org/2000/svg"
        width="24"
        height="24"
        viewBox="0 0 24 24"
        fill="none"
        stroke="currentColor"
        strokeWidth="2"
        strokeLinecap="round"
        strokeLinejoin="round"
      >
        <circle cx="7.5" cy="15.5" r="5.5" />
        <path d="m21 2-9.6 9.6" />
        <path d="m15.5 7.5 3 3L22 7l-3-3" />
      </svg>
    )
  }
  
  
  function UserIcon(props) {
    return (
      <svg
        {...props}
        xmlns="http://www.w3.org/2000/svg"
        width="24"
        height="24"
        viewBox="0 0 24 24"
        fill="none"
        stroke="currentColor"
        strokeWidth="2"
        strokeLinecap="round"
        strokeLinejoin="round"
      >
        <path d="M19 21v-2a4 4 0 0 0-4-4H9a4 4 0 0 0-4 4v2" />
        <circle cx="12" cy="7" r="4" />
      </svg>
    )
  }

  return (
    <>
      <div className="div">
        <div className="div-2">
          <div className="column">
            <div className="div-3">
              <img loading="lazy" srcSet="..." className="img" />
              <div className="div-4" />
            </div>
          </div>
          <div className="column-2">
            <div className="div-5">
              <div className="div-6">Iniciar Sesión</div>
              <div className="div-7">Si no tienes una cuenta registrada</div>
              <div className="div-8">
                Te puedes{" "}
                <span style={{ fontWeight: 600, color: "rgba(12,155,200,1)" }}>
                  Registrar Aqui!
                </span>
              </div>
              <div className="div-9">ID</div>
              <div className="div-10">
                <img loading="lazy" srcSet="..." className="img-2" />
                <div className="div-11">Ingresa tu codigo</div>
              </div>
              <div className="div-12" />
              <div className="div-13">Username</div>
              <div className="div-14">
                <img
                  loading="lazy"
                  src="https://cdn.builder.io/api/v1/image/assets/TEMP/984e985f0747a7ca77e6e61acac21130bc2cdb45738e1f35828757ad8b342435?"
                  className="img-3"
                />
                <div className="div-15">Ingresa tu usuario</div>
              </div>
              <div className="div-16" />
              <div className="div-17">Iniciar Sesión</div>
            </div>
          </div>
        </div>
      </div>
      <style jsx>{`
        .div {
          background-color: #fff;
          padding-right: 54px;
        }
        @media (max-width: 991px) {
          .div {
            padding-right: 20px;
          }
        }
        .div-2 {
          gap: 20px;
          display: flex;
        }
        @media (max-width: 991px) {
          .div-2 {
            flex-direction: column;
            align-items: stretch;
            gap: 0px;
          }
        }
        .column {
          display: flex;
          flex-direction: column;
          line-height: normal;
          width: 65%;
          margin-left: 0px;
        }
        @media (max-width: 991px) {
          .column {
            width: 100%;
          }
        }
        .div-3 {
          display: flex;
          flex-direction: column;
          overflow: hidden;
          position: relative;
          display: flex;
          min-height: 1024px;
          flex-grow: 1;
          justify-content: center;
        }
        @media (max-width: 991px) {
          .div-3 {
            max-width: 100%;
            margin-top: 40px;
          }
