import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom'; 
import loginImage from '../assets/loginImage.jpg';
import axios from 'axios';
import '../styles/LoginPage.css';

function Login() {
  const navigate = useNavigate(); 

  const [loginData, setLoginData] = useState({
    login: '',
    password: '',
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLoginData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
    setMessage('');
  };
 
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      console.log(loginData);
      const response = await axios.post('http://localhost:5430/api/user/logear', loginData);
      const usuario = response.data;
      console.log('Usuario autenticado:', usuario);

      localStorage.setItem('token', JSON.stringify(usuario));
      console.log('token: ', localStorage.getItem('token'))
      
      /*
      1 = Admin
      2 = Viewer
      3 = Agente
      */

      if(usuario.idRol === "1"){
        console.log('validacion: ', usuario.idRol)
        navigate('/main');
      }else if(usuario.idRol === "2"){
        console.log('validacion: ', usuario.idRol)
        navigate('/mainV');
      }else{
        console.log('validacion: ', usuario.idRol)
        navigate('/mainA');
      }
      
    } catch (error) {
      console.error('Error de inicio de sesión:', error);
      if (error.response && error.response.status === 401) {
        setMessage('Credenciales inválidas');
      } else {
        setMessage('Error de inicio de sesión');
      }
    }
  };

  return (
    <>
      <div className="div">
        <div className="div-2">
          <div className="column">
            <div className="div-3">
              <img loading="lazy" src={loginImage} className="img" />
            </div>
          </div>
          <div className="column-2">
            <div className="div-5">
              <div className="div-6">Iniciar Sesión</div>
              <div className="div-7">Si no tienes una cuenta registrada</div>
              <div className="div-8">
                Te puedes{" "}
                <span style={{ fontWeight: 600, color: "rgba(12,155,200,1)" }}>
                  Registrar Aqui!
                </span>
              </div>
              <div className="form">
                <form>
                  <label>ID</label>
                  <input className="login-input" type="text"name="login"value={loginData.login}onChange={handleChange}placeholder="Usuario"/>
                  <label>Clave</label>
                  <input className="login-input" type="text"name="login"value={loginData.login}onChange={handleChange}placeholder="Usuario"/>
                  <button>inisiar session</button>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );


}

export default Login;
